// Recursive function to parse JSON into plain text
const parseJSONToText = (data, depth = 0) => {
  const indent = "  ".repeat(depth); // Indentation for readability
  let text = "";

  if (typeof data === "object" && !Array.isArray(data) && data !== null) {
    // Handle objects
    for (const key in data) {
      text += `${indent}${key}:\n`;
      text += parseJSONToText(data[key], depth + 1);
    }
  } else if (Array.isArray(data)) {
    // Handle arrays
    data.forEach((item, index) => {
      text += `${indent}- [${index + 1}]\n`;
      text += parseJSONToText(item, depth + 1);
    });
  } else {
    // Handle primitive values (string, number, boolean, etc.)
    text += `${indent}${data}\n`;
  }

  return text;
};

